refsect2.programlisting:
struct ucil_rgb32 {
      unsigned char r;
      unsigned char g;
      unsigned char b;
      unsigned char a;
};

struct ucil_yuv {
      unsigned char y;
      unsigned char u;
      unsigned char v;
};

struct ucil_color {
      ucil_colorspace_t colorspace;

      union
      {
	    ucil_yuv_t yuv;
	    ucil_rgb24_t rgb24;
	    ucil_rgb32_t rgb32;
	    ucil_y8_t y8;
      };
};

unicap_status_t     ucil_check_version                  (unsigned int major,
                                                         unsigned int minor,
                                                         unsigned int micro);

informalexample.programlisting:
src.colorspace = UCIL_COLORSPACE_RGB24;
src.rgb24.r = 0xff;
src.rgb24.g = 0xff;
src.rgb24.b = 0xff;
dest.colorspace = UCIL_COLORSPACE_YUV;
ucil_convert_color( &dest, &src );
